import { useEffect, useState } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../firebase";
import { topics } from "../data/topics";

export default function Leaderboard() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const loadLeaderboard = async () => {
      const snapshot = await getDocs(collection(db, "users"));
      const data = snapshot.docs.map((doc) => {
        const progress = doc.data().progress || {};
        const score = Object.values(progress).filter(Boolean).length;
        return { name: doc.data().name, score };
      });
      setUsers(data.sort((a, b) => b.score - a.score));
    };
    loadLeaderboard();
  }, []);

  return (
    <div className="p-4 bg-white min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Leaderboard</h1>
      <ul className="space-y-2">
        {users.map((user, index) => (
          <li key={index} className="p-2 bg-blue-100 rounded flex justify-between">
            <span>{index + 1}. {user.name}</span>
            <span>{Math.round((user.score / topics.length) * 100)}%</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
