import { useEffect, useState } from "react";
import { topics } from "../data/topics";
import { db } from "../firebase";
import { doc, getDoc, updateDoc } from "firebase/firestore";
import ProgressBar from "../components/ProgressBar";
import TopicCard from "../components/TopicCard";
<a href="/leaderboard" className="text-blue-600 underline text-sm float-right">
  View Leaderboard
</a>
export default function Dashboard() {
  const [progress, setProgress] = useState({});
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const loadProgress = async () => {
      const userDoc = await getDoc(doc(db, "users", userId));
      setProgress(userDoc.data().progress || {});
    };
    loadProgress();
  }, [userId]);

  const toggleComplete = async (id) => {
    const updated = { ...progress, [id]: !progress[id] };
    setProgress(updated);
    await updateDoc(doc(db, "users", userId), { progress: updated });
  };

  const completedCount = Object.values(progress).filter(Boolean).length;
  const percentage = Math.round((completedCount / topics.length) * 100);

  return (
    <div className="p-4 bg-gray-50 min-h-screen">
      <h2 className="text-2xl font-bold mb-2 text-center">
        Welcome, {localStorage.getItem("name")}
      </h2>
      <div className="text-center mb-2 text-green-600 font-semibold">
        Progress: {percentage}%
      </div>
      <ProgressBar percentage={percentage} />
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        {topics.map((topic) => (
          <TopicCard
            key={topic.id}
            topic={topic}
            completed={progress[topic.id]}
            onToggle={() => toggleComplete(topic.id)}
          />
        ))}
      </div>
    </div>
  );
}
