import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { db } from "../firebase";
import { collection, getDocs, doc, setDoc } from "firebase/firestore";

export default function LoginPage() {
  const [name, setName] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!name.trim()) {
      alert("Please enter your name.");
      return;
    }

    // Check if user already exists
    const usersSnapshot = await getDocs(collection(db, "users"));
    const existingUser = usersSnapshot.docs.find(
      (doc) => doc.data().name.toLowerCase() === name.trim().toLowerCase()
    );

    let userId;

    if (existingUser) {
      userId = existingUser.id;
    } else {
      // Assign new unique number as ID
      const newUserNumber = usersSnapshot.size + 1;
      userId = `user${newUserNumber}`;
      await setDoc(doc(db, "users", userId), {
        name: name.trim(),
        progress: {},
      });
    }

    localStorage.setItem("userId", userId);
    localStorage.setItem("name", name.trim());
    navigate("/dashboard");
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-blue-50">
      <h1 className="text-3xl mb-4 font-bold">Welcome to Aptitude Trainer</h1>
      <input
        placeholder="Enter your name"
        className="p-2 border rounded w-64"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <button
        onClick={handleLogin}
        className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
      >
        Start
      </button>
    </div>
  );
}
